import { useState } from "react"
import "./paginacion.scss"

const atr = "<"
const del = ">"

export const Paginacion = ({ pagina, setPagina, maximo }) => {
   const [input, setInput] = useState(1)

   const nextPage = () => {
      setInput(input + 1);
      setPagina(pagina + 1);
   }

   const previusPage = () => {
      setInput(input - 1);
      setPagina(pagina - 1);
   }

   return (
      <div className="container">
         <button > {atr} </button>
         <input name="page" autoComplete="off" value={input} />
         <p> de {Math.ceil(maximo)} </p>
         <button onClick={nextPage}> {del} </button>
      </div>
   )
}
