import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux'
import { getRazas } from '../../redux/actions';

import NavBar from '../../components/navbar/navbar';
import Cards from '../../components/cards/cards';

import './home.css';

function Home() {
   const dispatch = useDispatch()
   const allRazas = useSelector((state) => state.allRazas)
   const [filtered, setFiltered] = useState(allRazas)
   const [searchName, setSearchName] = useState("")

   function handleChange(e) {
      e.preventDefault()
      setSearchName(e.target.value)
   }

   function handleSubmit(e) {
      e.preventDefault()
      const filtered = allRazas.filter((raza) => raza.name.includes(searchName))
      setFiltered(filtered)
   }

   useEffect(() => {
      dispatch(getRazas())
      //return (()=>{"clearDetail()"}) //? el return limpia el estado, hay que averiguar como funciona
   }, [dispatch])

   return (
      <div className='home'>
         <h2 className='title'>Proyecto Dogs</h2>
         <NavBar handleChange={handleChange} handleSubmit={handleSubmit} />
         <Cards allRazas={allRazas} />
      </div>
   );
}

export default Home;
