import { useState } from 'react';
import './form.css';

function Form() {
   const [input, setInput] = useState({
      name: "",
      weight: "",
      height: "",
      life_span: "",
      temperament: [""],
      image: "",
   })

   const [error, setError] = useState({
      name: "",
      weight: "",
      height: "",
      life_span: "",
      temperament: [""],
      image: "",
   })

   const validate = (input) => {
      let num = /\d/; // /^([^0-9]*)$/; // si tiene numero
      let symbol = /^([^@]*)$/; // si tiene simbolo?
      if (!num.test(input.name)) {
         setError({ ...error, name: "No se permiten numeros" })
      }
      // if (!symbol.test(input.name)) {
      //    setError({ ...error, name: "No se permiten simbolos" })
      //    return;
      // }
      setError({ ...error, name: "" })
   }


   function handleChange(e) {
      setInput({
         ...input,
         [e.target.value]: e.target.value,
      })
      setError(validate({
         ...input,
         [e.target.value]: e.target.value,
      }))

   }

   return (
      <div>
         <form onSubmit={null}>
            <div>
               <h1>Pagina de Agregado de Razas</h1>
               <label> Nombre: </label>
               <input type='text' placeholder='Nombre de raza' name="name" value={input.value} onChange={handleChange} /> <br />
               {<span>{error.name}</span> && error.name}
            </div>
            <div>
               <label></label>
               <input />
            </div>
            <div>
               <label></label>
               <input />
            </div>
            <div>
               <label></label>
               <input />
            </div>
            <button type="submit">Enviar</button>
         </form>
      </div>
   );
}

export default Form;
