import { useState } from "react"
import "./paginacion.scss"

const atr = "<"
const del = ">"

export const Paginacion = ({ pagina, setPagina, maximo }) => {
   const [input, setInput] = useState(1)

   const nextPage = () => {
      setInput(parseInt(input)) + 1;
      setPagina(parseInt(pagina)) + 1;
   }

   const previusPage = () => {
      setInput(input - 1);
      setPagina(pagina - 1);
   }

   const inputnt = (e) => {
      if (e.keycode == 13) {
         setPagina(parseInt(e.target.value))
         if (parseInt(e.target.value) < 1 || parseInt(e.target.value) > maximo || isNaN(parseInt(e.target.value))) {
            setPagina(1)
            setInput(1)
         } else {
            setPagina(parseInt(e.target.value));
         }
      }
   }

   const onChange = (e) => {
      setInput(e.target.value)
   }

   return (
      <div className="container">
         <button disabled={pagina === 1 || pagina < 1} onClick={previusPage}> {atr} </button>
         <input onChange={e => onChange(e)} inputnt={e => inputnt(e)} name="page" autoComplete="off" value={input} />
         <p> de {maximo} </p>
         <button disabled={pagina === maximo || pagina > maximo} onClick={nextPage}> {del} </button>
      </div>
   )
}
