const { GET_RAZAS } = require("../actions");

let initialState = { allRazas: [], allTemperaments: [] }

function rootReducer(state = initialState, accion) {
   switch (action.type) {
      case GET_RAZAS:
         return {
            ...state,
            allRazas: action.payload,
         }
      default:
         return state;
   }
}

module.exports = rootReducer;
