import { Link } from 'react-router-dom'

import './card.css';

function Card({ raza }) {
   const { name, image, weight, id, temperaments } = raza
   if (!name) { // si no hay name(undefined) muestra un error
      alert("That breed was not found, please press the RESET button")
   } else { // sino muestra las cards correspondientes
   return (
      <div key={id} className='card'>
         <Link className="link" to={`/home/${id}`}>
            <h2>{name}</h2>
            <img className='image' src={image} alt={name} />
            <p>{temperaments.length > 0
               ? `Temperaments: ${temperament
                  .slice(0, 3)
                  .map((temperament) => temperament.name)
                  .join(', ')}${temperaments.length > 3 ? '...' : ''}`
               : 'no hay temperamentos disponibles'
            }</p>
            <h3>Weight</h3><p> {weight} Kg</p>
         </Link>
      </div>
   );
}
}

export default Card;
